(2, 'report_listing', '', 2);

CREATE TABLE `{prefix}action_show` (
	`id` INT NOT NULL AUTO_INCREMENT,
	`page` VARCHAR( 100 ) NOT NULL ,
	`action_name` VARCHAR(255) NOT NULL default '',
	PRIMARY KEY (`id`)
);

