ALTER TABLE `{prefix}config_groups`
    ADD `name` varchar(50) NOT NULL DEFAULT '' AFTER `id`,
    ADD `plugin` varchar(50) NOT NULL DEFAULT '' AFTER `name`;

ALTER TABLE `{prefix}hooks`
    ADD `type` enum('php','html','smarty','plain') NOT NULL DEFAULT 'php' AFTER `name`,
    MODIFY `status` enum('active','inactive') NOT NULL DEFAULT 'active';
