('email_templates', 'listing_disapprove_body_html', '<h3>\r\n	Dear Sir/Madam,</h3>\r\n<p>\r\n	Your listing {your_site_url} has been disapproved in<em> <a href="{own_dir_url}">{own_site}</a></em>.</p>\r\n<p>\r\n	If you have questions, please feel free to ask us.</p>\r\n<p>\r\n	______________________________<br />\r\n	Thank you, <br />\r\n	{own_site} Administration Team<br />\r\n	{own_url}<br />\r\n	{own_email}</p>\r\n', '', 'textarea', 'HTML Body', '', '1', 0.74),
('email_templates', 'listing_disapprove_body_plaintext', 'Dear Sir/Madam,\r\n\r\nYour listing {your_site_url} has been disapproved in {own_site} Listings Directory.\r\n\r\nIf you have questions, please feel free to ask us.\r\n\r\n______________________________\r\nThank you,\r\n{own_site} Administration Team\r\n{own_url}\r\n{own_email}', '', 'textarea', 'Email Body', '', '0', 0.741),
